function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),i=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),i.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked"))}))}burgerMenu();const swiper=new Swiper(".swiper",{slidesPerView:1,spaceBetween:30,loop:!0,pagination:{el:".swiper-pagination",clickable:!0},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},scrollbar:{el:".swiper-scrollbar"}});